(function() {
  // Rain sound
  const rainAudio = document.createElement('audio');
  rainAudio.src = 'https://cdn.pixabay.com/audio/2021/08/08/audio_838420594f.mp3';
  rainAudio.loop = true;
  rainAudio.volume = 0.3;
  rainAudio.id = 'rain-audio';
  document.body.appendChild(rainAudio);
  rainAudio.play();

  // Click sound
  const clickSound = new Audio('https://sounds-mp3.com/mp3/0001787.mp3');
  clickSound.volume = 0.5;
  const clickableSelectors = ['[aria-label^=\"Direct messages\"]', '[class*=\"guilds\"]', '[class*=\"channel\"]', '[class*=\"listItem\"]'];
  document.addEventListener('click', function(e) {
    if (clickableSelectors.some(sel => e.target.closest(sel))) {
      clickSound.currentTime = 0;
      clickSound.play();
    }
  });

  // Time-based rain speed
  const hour = new Date().getHours();
  let speed = 1.0;
  if (hour >= 20 || hour <= 6) speed = 2.5;
  else if (hour >= 14 && hour <= 18) speed = 1.5;
  else speed = 0.8;

  const anim = document.querySelector('[data-lottie]');
  if (anim && anim._lottie) anim._lottie.setSpeed(speed);

  // Fade-in animation for new chat messages
  const style = document.createElement('style');
  style.innerHTML = `
    .messageListItem__1aYv- {
      opacity: 0;
      animation: fadeIn 0.5s ease forwards;
    }
    @keyframes fadeIn {
      from { opacity: 0; transform: translateY(5px); }
      to { opacity: 1; transform: translateY(0); }
    }
  `;
  document.head.appendChild(style);
})();
